import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export interface Data {
  foodData: FoodData;
  count: number;
  cartData?: CartData;
}

export interface FoodData {
  minimum_order_price: number;
  merchant_name: string;
  items: Items[];
  discounts: Discounts[];
}

export interface CartData {
  storedFoods: StoredFoods[],
  totalPrice?: number;
  isOverMinimum?: boolean;
}

export interface StoredFoods {
  name: string;
  quantitiy: number;
  price: number;
}

export interface Items {
  id: string;
  category_id: string;
  category_name: string;
  name: string;
  price: number;
}

export interface Discounts {
  id: string;
  name: string;
  discount_rate: number;
}

export const initialState = {
  foodData: {
    minimum_order_price: 0,
    merchant_name: "",
    items: [],
    discounts: []
  },
  count: 0,
  cartData: {
    storedFoods: [],
    totalPrice: 0,
    isOverMinimum: false
  },
  totalPrice: 0,
  isOverMinimum: false,
}

export const cartReducer = createSlice({
  name: 'cartReducer',
  initialState: initialState as Data,
  reducers: {
    STORE: (state, { payload }: PayloadAction<FoodData>) => {
      state.foodData = payload;
    },
    ADD: (state, { payload }: PayloadAction<string>) => {
      const target = state.foodData.items.filter((item: Items) => item.name === payload)[0];
      const targetObject = {
        name: target.name,
        quantitiy: 1,
        price: target.price
      }
      state.cartData?.storedFoods.push(targetObject);
      state.count++;
    },
    DELETE: (state, { payload }: PayloadAction<string>) => {
      let deletedCart = state.cartData?.storedFoods
      deletedCart = state.cartData?.storedFoods.filter((item:StoredFoods) => item.name !== payload);
    },
    INCREASE: (state, { payload }: PayloadAction<string>) => {
      const target = state.cartData?.storedFoods.filter((item:StoredFoods) => item.name === payload)[0] !;
      target.quantitiy++;
    },
    DECREASE: (state, { payload }: PayloadAction<string>) => {
      const target = state.cartData?.storedFoods.filter((item:StoredFoods) => item.name === payload)[0] !;
      if (target.quantitiy === 0) return;
      target.quantitiy--;
    }
  }
});
