import { useState } from "react";
import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import { FoodData } from "../Reducers/cartReducer";
import axios from 'axios';

interface FoodDataError{
  errorMessage: string
}

export const fetchFoodData = createAsyncThunk<
FoodData,number,{ rejectValue: FoodDataError}>
  ('fetchFoodData', async () => {
    try {
      const response = await axios.get("https://us-central1-react-baemin.cloudfunctions.net/merchantInfo")
      return response.data;
    } catch(error) {
      return { errorMessage: error }
    }
  }
)
