import { createSlice, PayloadAction } from '@reduxjs/toolkit';
// import rootReducer from '.';

interface Data {
  data: FoodData;
  clickedFood: object;
  totalPrice: number;
  isOverMinimum: boolean;
}

interface FoodData {
  discounts: Discounts[];
  items: Items[];
  merchant_name: string;
  minimum_order_price: number;
}

interface Discounts {
  id: string;
  name: string;
  discount_rate: number;
}

interface Items {
  id: string;
  category_id: string;
  category_name: string;
  name: string;
  price: number;
}

const initialState = {
  data: {},
  clickedFood: {},
  totalPrice: 0,
  isOverMinimum: false,
}

export const cartReducer = createSlice({
  name: 'cartReducer',
  initialState: initialState,
  reducers: {
    STORE: (state, { payload }: PayloadAction<FoodData>) => {
      state = payload
      return state;
    },
    ADD: (state, { payload }: PayloadAction<FoodData>) => {
      return state;
    },
    DELETE: (state, { payload }: PayloadAction<FoodData>) => {
      return state;
    },
    INCREASE: (state, { payload }: PayloadAction<FoodData>) => {
      return state;
    },
    DECREASE: (state, { payload }: PayloadAction<FoodData>) => {
      return state;
    }
  }
});

export const { STORE, ADD, DELETE, INCREASE, DECREASE } = cartReducer.actions;
