import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { fetchFoodData } from '../Async/fetchFoodData';

import { FoodData, Items } from './cartReducer';
import { FoodsTypeProps } from '../Components/FoodsCategories';

export interface InitialFoodData {
  foodData: FoodData;
  sortedFoodsData: FoodsTypeProps[];
  isLoading: boolean;
  error?: unknown;
}

const initialState = {
  foodData: {
    minimum_order_price: 0,
    merchant_name: "",
    items: [],
    discounts: []
  },
  sortedFoodsData: [],
  isLoading: false,
  error: []
}

export const foodDataReducer = createSlice({
  name: 'foodDataReducer',
  initialState: initialState as InitialFoodData,
  reducers: {
    STORE: (state, { payload }: PayloadAction<FoodData>) => {
      const foodItemsNameArray = payload.items.map((item: Items) :string=> {
        return item.category_name;
      });
      const FOODS_TYPES = Array.from(new Set([...foodItemsNameArray]));
      state.sortedFoodsData = FOODS_TYPES.map((type:string) : [string,Items[]]=> {
        const FOODS_LIST= payload.items.filter((item: Items) => item.category_name === type) 
        return [type,FOODS_LIST];
      });
    }
  },
  extraReducers: (builder) => {
    builder.addCase(fetchFoodData.pending, (state) => {
      state.isLoading = true;
    })
    .addCase(fetchFoodData.fulfilled, (state, { payload }) => {
      state.isLoading = false;
      state.foodData = payload;
    })
    .addCase(fetchFoodData.rejected, (state, { payload }) => {
      state.isLoading = false;
      state.error = payload;
    })
  }
});
